import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

const socket = io('http://localhost:5000', { transports: ['websocket', 'polling', 'flashsocket'] });

const AdminDashboard = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/data').then(response => setData(response.data));

    socket.on('dataUpdate', (newData) => {
      setData(prevData => {
        const updatedData = [...prevData, newData];
        setTimeout(() => {
          const element = document.getElementById(`item-${updatedData.length - 1}`);
          if (element) {
            element.style.opacity = '1';
            element.style.transform = 'translateY(0)';
          }
        }, 100);
        return updatedData;
      });
    });

    return () => {
      socket.off('dataUpdate');
    };
  }, []);

  return (
    <div className="container mt-5">
      <h1 className="mb-4">Admin Dashboard</h1>
      <div className="list-container" style={{ maxHeight: '400px', overflowY: 'auto' }}>
        <ul className="list-group">
          {data.map((item, index) => (
            <li 
              key={index} 
              id={`item-${index}`} 
              className="list-group-item" 
              style={{ 
                transition: 'transform 0.5s ease-out, opacity 0.5s ease-out',
                opacity: '0',
                transform: 'translateY(-20px)'
              }}
            >
              <strong>{item.name}</strong> - {item.phone} - {item.location} - {item.message}
            </li>
          ))}
        </ul>
      </div>
      <style>
        {`
          .list-group-item:nth-child(even) {
            background-color: #f8f9fa;
          }
          .list-group-item {
            opacity: 0;
            transform: translateY(-20px);
          }
          .list-group-item.appear {
            opacity: 1;
            transform: translateY(0);
          }
        `}
      </style>
    </div>
  );
};

export default AdminDashboard;
