import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import logo from './Hirdaramanilogo.png'; // Adjust the path based on your project structure

const UserForm = () => {
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [location, setLocation] = useState('Gampaha');
  const [message, setMessage] = useState('');

  useEffect(() => {
    const savedName = localStorage.getItem('name');
    const savedPhone = localStorage.getItem('phone');
    const savedLocation = localStorage.getItem('location');

    if (savedName) setName(savedName);
    if (savedPhone) setPhone(savedPhone);
    if (savedLocation) setLocation(savedLocation);
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    localStorage.setItem('name', name);
    localStorage.setItem('phone', phone);
    localStorage.setItem('location', location);

    try {
      await axios.post('http://localhost:5000/submit', { name, phone, location, message });
      toast.success('Form submitted successfully!', {
        position: toast.POSITION.TOP_CENTER,
      });
      setMessage(''); // Clear only the message input field
    } catch (error) {
      console.error('Error submitting form', error);
      toast.error('Failed to submit the form', {
        position: toast.POSITION.TOP_CENTER,
      });
    }
  };

  return (
    <>
      <style>
        {`
          .form-container {
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
            min-height: 100vh;
            background-color: #f8f9fa;
            text-align: center;
          }
          .form-card {
            width: 100%;
            max-width: 350px;
            padding: 20px;
            margin: 20px;
            background-color: #fff;
            border-radius: 10px;
            box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
          }
          .form-card label {
            font-weight: bold;
          }
          .form-card button {
            width: 100%;
          }
          .logo {
            max-width: 150px;
            margin-bottom: 20px;
          }
          .heading {
            font-size: 1.2rem;
            margin-bottom: 20px;
          }
        `}
      </style>
      <div className="form-container">
        <img src={logo} alt="Hirdaramani Logo" className="logo" />
        <h1 className="heading">H-Beat Radio Live Chat</h1>
        <div className="form-card">
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="name" className="form-label">Name:</label>
              <input type="text" className="form-control" id="name" value={name} onChange={(e) => setName(e.target.value)} required />
            </div>
            <div className="mb-3">
              <label htmlFor="phone" className="form-label">Phone Number:</label>
              <input type="text" className="form-control" id="phone" value={phone} onChange={(e) => setPhone(e.target.value)} required />
            </div>
            <div className="mb-3">
              <label htmlFor="location" className="form-label">Factory Location:</label>
              <select className="form-select" id="location" value={location} onChange={(e) => setLocation(e.target.value)} required>
                <option value="Gampaha">Gampaha</option>
                <option value="Kandy">Kandy</option>
                <option value="Colombo">Colombo</option>
              </select>
            </div>
            <div className="mb-3">
              <label htmlFor="message" className="form-label">Message:</label>
              <textarea className="form-control" id="message" value={message} onChange={(e) => setMessage(e.target.value)} required></textarea>
            </div>
            <button type="submit" className="btn btn-primary">Submit</button>
          </form>
        </div>
      </div>
      <ToastContainer />
    </>
  );
};

export default UserForm;
