import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

const socket = io('http://localhost:5000', { transports: ['websocket', 'polling', 'flashsocket'] });

const AdminDashboard = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/data').then(response => setData(response.data));

    socket.on('dataUpdate', (newData) => {
      setData(prevData => {
        const updatedData = [...prevData, newData];
        setTimeout(() => {
          const element = document.getElementById(`item-${updatedData.length - 1}`);
          if (element) {
            element.classList.add('appear');
          }
        }, 100);
        return updatedData;
      });
    });

    return () => {
      socket.off('dataUpdate');
    };
  }, []);

  return (
    <div className="container mt-5">
      <style>
        {`
          .container {
            max-width: 600px;
            margin: 0 auto;
          }

          ul.list-group {
            max-height: 400px; /* Limit the height */
            overflow-y: auto;  /* Add vertical scroll */
          }

          .list-group-item {
            transition: transform 0.5s ease-out, opacity 0.5s ease-out;
            opacity: 0;
          }

          .list-group-item:nth-child(even) {
            background-color: #f8f9fa; /* Bootstrap's light gray */
          }

          .list-group-item.appear {
            opacity: 1;
            transform: translateY(0);
          }

          .list-group-item.disappear {
            opacity: 0;
            transform: translateY(-20px);
          }
        `}
      </style>
      <h1 className="mb-4">Admin Dashboard</h1>
      <ul className="list-group">
        {data.map((item, index) => (
          <li key={index} id={`item-${index}`} className="list-group-item">
            <strong>{item.name}</strong> - {item.phone} - {item.location} - {item.message}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminDashboard;
